if(isset($_POST['BackUp_cashbook']))
{   define('DATABASE','vnrseed2_cashbook');
    $link = mysql_connect(HOST,USER,PASS);
    $dblink = mysql_select_db(DATABASE,$link);
	
	$tables = '*';
	if($tables == '*')
	{
		$tables = array();
		$result = mysql_query('SHOW TABLES');
		while($row = mysql_fetch_row($result))
		{ $tables[] = $row[0]; }
	}
	else
	{ $tables = is_array($tables) ? $tables : explode(',',$tables);	}
	
	//cycle through
	foreach($tables as $table)
	{
		$result = mysql_query('SELECT * FROM '.$table);
		$num_fields = mysql_num_fields($result);
		
		//$return.= 'DROP TABLE '.$table.';';
		$row2 = mysql_fetch_row(mysql_query('SHOW CREATE TABLE '.$table));
		$return.= "\n\n".$row2[1].";\n\n";
		
		for ($i = 0; $i < $num_fields; $i++) 
		{
			while($row = mysql_fetch_row($result))
			{
				$return.= 'INSERT INTO '.$table.' VALUES(';
				for($j=0; $j<$num_fields; $j++) 
				{
					$row[$j] = addslashes($row[$j]);
					$row[$j] = ereg_replace("\n","\\n",$row[$j]);
					if (isset($row[$j])) { $return.= '"'.$row[$j].'"' ; } else { $return.= '""'; }
					if ($j<($num_fields-1)) { $return.= ','; }
				}
				$return.= ");\n";
			}
		}
		$return.="\n\n\n";
	}
	
	//save file
	$handle = fopen('Back/agrimatr_cashbook_'.date("d-m-Y").'.sql','w+');
	fwrite($handle,$return);
	fclose($handle);
	
	$file_name = 'agrimatr_cashbook_'.date("d-m-Y").'.sql';
	$file_path = 'Back/agrimatr_cashbook_'.date("d-m-Y").'.sql';
	header("Pragma: public");
    header("Expires: 0");
    header("Cache-Control:must-revalidate, post-check=0, pre-check=0");
	header('Content-type: text/plain');
    header('Content-Length: '.filesize($file));
    header("Content-Disposition:attachment;filename=\"".$file_name."\";");
    header("Content-Transfer-Encoding:binary");
    $file = & fopen($file_path, 'rb');
    if($file){ fpassthru($file); exit; }else{echo $err; } 
}

