function check_value(me)
{
	var Amount = me.charAt(me.length-1);
	var Numfilter=/^[0-9.]+$/;
	var test_num = Numfilter.test(Amount);
	if(!test_num){
		alert("Please Enter Only numeric data!");
		if(me.length>0){
			document.getElementById("openingAmt").value=document.getElementById("openingAmt").value.substring(0,me.length-1);
		}
		return false;
	} else {
		return true;
	}
}

function ValidateForm()
{
	if(document.getElementById("location").value==0){
		alert("Please input/select Location !");
		return false;
	}
	if(document.getElementById("cihName").value==0){
		alert("Please input/select Cash-in-hand Name !");
		return false;
	}
	if(document.getElementById("openingDate").value!=""){
		if(!checkdate(document.OpBalance.openingDate)){
			return false;
		} else {
			var no_of_days1 = getDaysbetween2Dates(document.OpBalance.openingDate,document.OpBalance.endYear);
			if(no_of_days1 < 0){
				alert("Opening date wrongly selected. Please correct and submit again !");
				return false;
			} else {
				var no_of_days2 = getDaysbetween2Dates(document.OpBalance.startYear,document.OpBalance.openingDate);
				if(no_of_days2 < 0){
					alert("Opening date wrongly selected. Please correct and submit again !");
					return false;
				} else {
					var no_of_days3 = getDaysbetween2Dates(document.OpBalance.openingDate,document.OpBalance.minDate);
					if(no_of_days3 < 0){
						alert("Opening date wrongly selected. Please correct and submit again.\n"+
						"Starting date was "+document.getElementById("minDate").value+", so higher date is not acceptable !");
						return false;
					}
				}
			}
		}
	} else if(document.getElementById("openingDate").value==""){
		alert("Please input/select opening date !");
		return false;
	}
/*	if(document.getElementById("company").value==0){
		alert("Please input/select company !");
		return false;
	}*/
	document.getElementById("submit").style.display='none';
}

function get_min_date(value1)
{
	var url = 'get_min_date.php';
	var pars = 'id='+value1;
	var myAjax = new Ajax.Request(
	url, 
	{
		method: 'post', 
		parameters: pars, 
		onComplete: show_min_date
	});
}
function show_min_date(originalRequest)
{
	document.getElementById('minDate').value=originalRequest.responseText;
}

function get_cash_in_hand(value1)
{
	var url = 'get_cash_in_hand1.php';
	var pars = 'id='+value1;
	var myAjax = new Ajax.Request(
	url, 
	{
		method: 'post', 
		parameters: pars, 
		onComplete: show_cash_in_hand
	});
}
function show_cash_in_hand(originalRequest)
{
	document.getElementById('tdCIH').innerHTML=originalRequest.responseText;
	get_min_date(document.getElementById('cihName').value);
	get_company(document.getElementById('location').value);
}

function get_company(value1)
{
	var url = 'get_company.php';
	var pars = 'id='+value1;
	var myAjax = new Ajax.Request(
	url, 
	{
		method: 'post', 
		parameters: pars, 
		onComplete: show_company
	});
}
function show_company(originalRequest)
{
	document.getElementById('tdComp').innerHTML=originalRequest.responseText;
}
