function ValidateForm()
{
	if(document.getElementById("userId").value==""){
		alert("Please input user ID !");
		return false;
	}
	if(document.getElementById("userPwd").value==""){
		alert("Please input user password !");
		return false;
	}
	if(document.getElementById("userRePwd").value==""){
		alert("Please input user confirm password !");
		return false;
	}
	if(document.getElementById("userPwd").value!=document.getElementById("userRePwd").value){
		alert("User password and confirm password mismatch !");
		return false;
	}
	if(document.getElementById("userType").value=="S"){
		alert("Please select user's type !");
		return false;
	}
	if(document.getElementById("userLocation").value==0){
		alert("Please select user's location !");
		return false;
	}
	if(document.getElementById("userType").value=="U" && document.getElementById("cihName").value==0){
		alert("Please select Cash Name !");
		return false;
	}
	document.getElementById("submit").style.display='none';
}

function get_cashinhand(value1)
{
	var url = 'get_cashinhand1.php';
	var pars = 'id='+value1;
	var myAjax = new Ajax.Request(
	url, 
	{
		method: 'post', 
		parameters: pars, 
		onComplete: show_cashinhand
	});
}
function show_cashinhand(originalRequest)
{
	document.getElementById('tdCIH').innerHTML=originalRequest.responseText;
}
