<script type="text/javascript" language="javascript">
function setPrintPage()
{
	if(amount_in_words()){
		if(document.getElementById('select1').checked){
			var prnThis = "printvoucher.php?v="+document.getElementById('vid').value+'&a='+document.getElementById('container').value;
			var prnDoc = document.getElementsByTagName('Link');
			//prnDoc[0].setAttribute('href', prnThis);
			//window.parent.print();
			window.location.href=prnThis;
		} else if(document.getElementById('select2').checked){
			//do nothing and close the window
		}
	}
	//window.close();
}

function amount_in_words() {
	if(Number(document.getElementById('rupees').value) >=0){
		//do nothing
	} else {
		alert('wrong Number cannot be converted');
		return false;
	}
	
	var amount = document.getElementById('rupees').value.split(".");
	var rupees = Math.floor(amount[0]);
	if(amount.length>1){
		var paise = Math.floor(amount[1]);
	} else {
		var paise = 0;
	}
	
	if(amount.length>1){
		if(amount[1].length>2){
			alert('wrong Amount cannot be converted');
			return false;
		}
	}
	if(amount[0].length>9){
		alert('Oops!!!! the Number is too big to covert');
		return false;
	}
	if(Number(rupees)==0 && Number(paise)==0){
		document.getElementById('container').innerHTML='Rupees Zero Only';
		return false;
	}
	
	var iWords=["Zero", " One", " Two", " Three", " Four", " Five", " Six", " Seven", " Eight", " Nine"];
	var ePlace=['Ten', ' Eleven', ' Twelve', ' Thirteen', ' Fourteen', ' Fifteen', ' Sixteen', ' Seventeen', ' Eighteen', ' Nineteen'];
	var tensPlace=['dummy', ' Ten', ' Twenty', ' Thirty', ' Forty', ' Fifty', ' Sixty', ' Seventy', ' Eighty', ' Ninety' ];
	
	//Paise convert in words
	if(Number(paise)>0){
		var numReversed = amount[1].split("");
		var actnumber = numReversed;
		var paiseinWords = "";
		
		if(actnumber[0]==0 && actnumber[1]==0){
			paiseinWords='';
		} else if(actnumber[0]==0 && actnumber[1]!=0){
			paiseinWords=' and Paise'+iWords[actnumber[1]];
		} else if(actnumber[0]!=0 && actnumber[1]==0){
			paiseinWords=' and Paise'+tensPlace[actnumber[0]];
		} else if(actnumber[0]==1 && actnumber[1]==0){
			paiseinWords=' and Paise '+ePlace[actnumber[1]];
		} else if(actnumber[0]==1 && actnumber[1]!=0){
			paiseinWords=' and Paise'+ePlace[actnumber[1]];
		} else if(actnumber[0]>1 && actnumber[1]!=0){
			paiseinWords=' and Paise'+tensPlace[actnumber[0]]+iWords[actnumber[1]];
		}
	}
	
	//Now Rupees convert in words
	var numReversed = amount[0].split("");
	var actnumber = numReversed.reverse();
	var iWordsLength = numReversed.length;
	var totalWords = "";
	var inWords = new Array();
	var rupeesinWords = "Rupees ";
	var j = 0;
	for(var i=0; i<iWordsLength; i++){
		switch(i)
		{
		case 0:
			if(actnumber[i]==0 || actnumber[i+1]==1 ){
				inWords[j]='';
			} else {
				inWords[j]=iWords[actnumber[i]];
			}
			inWords[j]=inWords[j];
			break;
		case 1:
			tens_complication();
			break;
		case 2:
			if(actnumber[i]==0) {
				inWords[j]='';
			} else if(actnumber[i-1]!=0 && actnumber[i-2]!=0) {
				inWords[j]=iWords[actnumber[i]]+' Hundred and';
			} else {
				inWords[j]=iWords[actnumber[i]]+' Hundred ';
			}
			break;
		case 3:
			if(actnumber[i]==0 || actnumber[i+1]==1) {
				inWords[j]='';
			} else {
				inWords[j]=iWords[actnumber[i]];
			}
			if(actnumber[i+1] != 0 || actnumber[i] > 0){
				inWords[j]=inWords[j]+" Thousand";
			}
			break;
		case 4:
			tens_complication();
			break;
		case 5:
			if(actnumber[i]==0 || actnumber[i+1]==1) {
				inWords[j]='';
			} else {
				inWords[j]=iWords[actnumber[i]];
			}
			if(actnumber[i+1] != 0 || actnumber[i] > 0){
				inWords[j]=inWords[j]+" Lakh";
			}
			break;
		case 6:
			tens_complication();
			break;
		case 7:
			if(actnumber[i]==0 || actnumber[i+1]==1 ){
				inWords[j]='';
			} else {
				inWords[j]=iWords[actnumber[i]];
			}
			inWords[j]=inWords[j]+" Crore";
			break;
		case 8:
			tens_complication();
			break;
		default:
			break;
		}
		j++;
	}
	
	function tens_complication() {
		if(actnumber[i]==0){
			inWords[j]='';
		} else if(actnumber[i]==1){
			inWords[j]=ePlace[actnumber[i-1]];
		} else {
			inWords[j]=tensPlace[actnumber[i]];
		}
	}
	inWords.reverse();
	for(i=0; i<inWords.length; i++) {
		rupeesinWords+=inWords[i];
	}
	if(Number(rupees)==0 && Number(paise)>0){
		rupeesinWords += 'Zero'+paiseinWords+' Only.';
	} else if(Number(rupees)>0 && Number(paise)==0){
		rupeesinWords += ' Only.';
	} else if(Number(rupees)>0 && Number(paise)>0){
		rupeesinWords += paiseinWords+' Only.';
	}
	document.getElementById('container').value=rupeesinWords;
	return true;
}
